mapper.insert:

SELECT SEQ_PRTF_BOARD.NEXTVAL FROM DUAL	


INSERT INTO PRTF_BOARD (
	user_id
	,prtf_num
	,prtf_title
	,prtf_con
	,frm_dt
	,to_dt
	,position
	,idea
	,url
	,hit
	,category
	,ins_dt
) VALUES (
	#{user_id}
	,#{prtf_num}
	,#{prtf_title}
	,#{prtf_con}
	,to_date(#{frm_dt}, 'yyyy/mm/dd')
	,to_date(#{to_dt}, 'yyyy/mm/dd')
	,#{position}
	,#{idea}
	,#{url}
	,0
	,#{category}
	,sysdate)

mapper.update:

UPDATE PRTF_BOARD SET
	prtf_title = #{prtf_title}
	,prtf_con = #{prtf_con}
	,frm_dt = #{frm_dt}
	,to_dt = #{to_dt}
	,position = #{position}
	,idea = #{idea}
	,category = #{category}
	,url = #{url}
	,upt_dt = sysdate
WHERE prtf_num = #{prtf_num}
