mapper.insert:

SELECT SEQ_USER_INFO.NEXTVAL FROM DUAL	


INSERT INTO user_info (
	user_nm
	,user_count
	,user_id
	,user_pw
	,user_mail
	,user_addr
	,user_tel
	,join_dt
	,user_auth
) VALUES (
	#{user_nm}
	,#{user_count}
	,#{user_id}
	,#{user_pw}
	,#{user_mail}
	,#{user_addr}
	,#{user_tel}
	,sysdate
	,'U'
)

mapper.update:

UPDATE user_info
SET
	user_mail = #{user_mail}
	,user_pw = #{user_pw}
	,zipcode = #{zipcode}
	,addr01 = #{addr01}
	,addr02 = #{addr02}
	,addr03 = #{addr03}
	,user_tel = #{user_tel}
	,url = #{url}
	,upt_dt = sysdate
WHERE user_id = #{user_id}


UPDATE user_info SET
	user_auth = #{user_auth}
	,upt_dt = sysdate
WHERE user_id = #{user_id}

mapper.select:

SELECT COUNT(*)
FROM user_info
WHERE user_id = #{user_id}
	AND user_pw = #{user_pw}


SELECT user_id
FROM user_info
WHERE user_nm = #{user_nm}
	AND user_mail = #{user_mail}


SELECT user_pw
FROM user_info
WHERE user_id = #{user_id}
	AND user_nm = #{user_nm}
